/* -----------------------------------------------------------------
   Action « Calculer distance totale »
   - additionne la longueur de toutes les LINE_STRING Draw
   - résultat arrondi à l’entier (mètres)
   - stocke la valeur dans variables['272c99b3-1431-43b8-8862-cd964e169351'].value
   - puis nettoie : lignes + contrôle Draw
------------------------------------------------------------------ */

if (window.draw && typeof turf !== 'undefined') {

  /* 1)  Longueur totale */
  const features   = window.draw.getAll().features;
  const totalKm    = features.reduce((sum, f) => sum + turf.length(f, { units: 'kilometers' }), 0);
  const totalMeters = Math.round(totalKm * 1000);   // conversion km → m + arrondi

  /* 2)  Mise à jour de la variable cible */
  const v = variables['272c99b3-1431-43b8-8862-cd964e169351'];
  if (v) v.value = totalMeters;

  console.log('Distance totale calculée :', totalMeters, 'm');

  /* 3)  Nettoyage complet : lignes + contrôle */
  window.draw.deleteAll();           // supprime toutes les lignes
  map2.removeControl(window.draw);   // retire l’UI Draw
  window.draw = null;                // libère l’instance
}

