  const map = new mapboxgl.Map({
    container: 'map', // ID de votre div
    style: 'mapbox://styles/mapbox/light-v10',
    center: [2.35, 48.85],
    zoom: 13
  });

  const ZOOM_THRESHOLD = 16;
  let debounceTimeout = null;
  let measurementMarker = null; // Marqueur affichant la mesure (aire ou distance)

  // Charger Turf.js dynamiquement pour les calculs géospatiaux
  const turfScript = document.createElement("script");
  turfScript.src = "https://cdn.jsdelivr.net/npm/@turf/turf/turf.min.js";
  document.head.appendChild(turfScript);

  // Charger Mapbox Draw CSS dynamiquement
  const cssDraw = document.createElement("link");
  cssDraw.href =
    "https://api.mapbox.com/mapbox-gl-js/plugins/mapbox-gl-draw/v1.4.2/mapbox-gl-draw.css";
  cssDraw.rel = "stylesheet";
  document.head.appendChild(cssDraw);

  // Charger Mapbox Draw JS dynamiquement
  const scriptDraw = document.createElement("script");
  scriptDraw.src =
    "https://api.mapbox.com/mapbox-gl-js/plugins/mapbox-gl-draw/v1.4.2/mapbox-gl-draw.js";
  scriptDraw.onload = () => {
    // Initialiser Mapbox Draw avec uniquement les outils polygone, ligne et poubelle
    const Draw = new MapboxDraw({
      displayControlsDefault: false,
      controls: {
        polygon: true,
        line_string: true,
        trash: true,
      },
    });
    map.addControl(Draw);

    map.on("draw.create", () => {
      const data = Draw.getAll();
      if (data.features.length === 0) return;

      // Récupérer la feature dessinée
      const feature = data.features[0];

      // Supprimer d'éventuelles mesures précédentes
      if (measurementMarker) {
        measurementMarker.remove();
        measurementMarker = null;
      }
      // Si un polygone existe, supprimer toute ligne statique préexistante
      if (feature.geometry.type === "Polygon") {
        if (map.getLayer("static-measurement-layer")) {
          map.removeLayer("static-measurement-layer");
          map.removeSource("static-measurement");
        }
      }
      // Si c'est une ligne, supprimer toute zone statique préexistante
      else if (feature.geometry.type === "LineString") {
        if (map.getLayer("static-polygon-layer")) {
          map.removeLayer("static-polygon-layer");
          map.removeSource("static-polygon");
        }
      }

      // Si le type est un polygone : calculer l'aire
      if (feature.geometry.type === "Polygon") {
        const area = turf.area(feature); // Aire en m²
        const centroid = turf.centroid(feature); // Calcul du centroïde
        const coordinates = centroid.geometry.coordinates;

        const el = document.createElement("div");
        el.style.background = "rgba(0,0,0,0.7)";
        el.style.color = "#fff";
        el.style.padding = "5px 8px";
        el.style.borderRadius = "3px";
        el.style.fontSize = "12px";
        el.style.whiteSpace = "nowrap";
        el.innerHTML = `Aire: ${area.toFixed(2)} m²`;

        measurementMarker = new mapboxgl.Marker({ element: el, anchor: "center" })
          .setLngLat(coordinates)
          .addTo(map);

        // Finaliser le dessin en supprimant la feature active de Draw
        Draw.deleteAll();

        // Affichage du polygone en couche statique
        if (map.getSource("static-polygon")) {
          map.getSource("static-polygon").setData(feature);
        } else {
          map.addSource("static-polygon", {
            type: "geojson",
            data: feature,
          });
          map.addLayer({
            id: "static-polygon-layer",
            type: "fill",
            source: "static-polygon",
            paint: {
              "fill-color": "#00ff00", // Vert fluo
              "fill-opacity": 0.3,
              "fill-outline-color": "#000",
            },
          });
        }
      }
      // Si le type est une ligne : calculer la distance
