/* 2) Fonction qui crée (ou ré-utilise) Mapbox Draw et passe en mode LINE */
function startDistanceDraw() {
  if (!window.map2 || typeof MapboxDraw === 'undefined') return;

  if (!window.draw) {
    window.draw = new MapboxDraw({
      displayControlsDefault: false,
      controls: { line_string: true, trash: false }  // ← outil ligne seule
    });
    map2.addControl(window.draw);
