function startSurfaceDraw() {
  if (!window.map2 || typeof MapboxDraw === 'undefined') return;

  if (!window.draw) {
    window.draw = new MapboxDraw({
      displayControlsDefault: false,
      controls: { polygon: true, trash: false }
    });
    map2.addControl(window.draw);
  }

  window.draw.deleteAll();           // vide les anciens
  window.draw.changeMode('draw_polygon');

  // Pour éviter de dupliquer les listeners
  if (!window._drawListenerAdded) {
    map2.on('draw.create', () => {
      setTimeout(() => {
        window.draw.changeMode('draw_polygon');
      }, 200); // laisser le temps au polygone d’être validé
    });
    window._drawListenerAdded = true;
  }
}

