function formatToEuro(value) {
    // Vérifier si la valeur est un nombre valide
    if (isNaN(value) || value === null) {
        return ''; // Retourner une chaîne vide si la valeur n'est pas valide
    }

    // Convertir en entier pour éviter les décimales
    const integerValue = Math.floor(value);

    // Convertir le nombre en chaîne de caractères avec des espaces tous les trois chiffres
    const formattedValue = integerValue.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ' ');

    // Ajouter le symbole de l'euro au début
    return `€ ${formattedValue}`;
}

// Exemple d'utilisation

return formatToEuro(number);

function formatAdresse(adresse) {
  // On essaie de séparer le numéro + rue, le code postal, et la ville
  const regex = /^(.+?)\s(\d{5})\s(.+)$/;
  const match = adresse.match(regex);

  if (match) {
    const rue = match[1];
    const codePostal = match[2];
    const ville = match[3];
    return `${rue}, ${codePostal}, ${ville}`;
  } else {
    return adresse; // Si le format n’est pas reconnu, on renvoie l'adresse telle quelle
  }
}

return formatAdresse(adress);

function formatAddressForFilename(address) {
  const formatted = address
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")       // Enlève les accents
    .replace(/[^\w\s-]/g, "")              // Supprime les caractères spéciaux
    .replace(/[\s,]+/g, "-")               // Espaces et virgules → tirets
    .replace(/-+/g, "-")                   // Nettoie les doubles tirets
    .toLowerCase()
    .trim();

  return `xtensio-${formatted}.pdf`;
}
return formatAddressForFilename(address)

