function formatAddressForFilename(address) {
  const formatted = address
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "")       // Enlève les accents
    .replace(/[^\w\s-]/g, "")              // Supprime les caractères spéciaux
    .replace(/[\s,]+/g, "-")               // Espaces et virgules → tirets
    .replace(/-+/g, "-")                   // Nettoie les doubles tirets
    .toLowerCase()
    .trim();

  return `xtensio-${formatted}.pdf`;
}
