// Déclarez la variable globale "variables" (si elle n'existe pas déjà)
window.variables = window.variables || {};

// Définition des niveaux de zoom cibles pour chaque niveau administratif
const TARGET_ZOOM = {
  region: 6,       // Niveau cible pour la région
  departement: 8,  // Niveau cible pour le département
  commune: 14      // Niveau cible pour la commune
};

// Fonction d'ajout du curseur "pointer" pour une couche donnée
const addCursorChange = (layerId, map) => {
  map.on("mouseenter", layerId, () => {
    map.getCanvas().style.cursor = "pointer";
  });
  map.on("mouseleave", layerId, () => {
    map.getCanvas().style.cursor = "";
  });
};

// Charger Mapbox GL CSS dynamiquement
const css = document.createElement("link");
css.href = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.css";
css.rel = "stylesheet";
document.head.appendChild(css);

// Charger Mapbox GL JS dynamiquement
const script = document.createElement("script");
script.src = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.js";
script.onload = () => {
  mapboxgl.accessToken =
    'pk.eyJ1Ijoib2FzMTg5MyIsImEiOiJjbGZkNjNtcmwwOTlzNDBscjNpYmZ4cWhwIn0.oGxBPKe7HPex0VJFW_Usow';

  // Initialisation de la carte (niveau global de départ)
  const map = new mapboxgl.Map({
    container: 'map', // La div avec l'ID "map" doit être présente dans votre HTML
    style: 'mapbox://styles/mapbox/light-v10',
    center: [2.35, 48.85],
    zoom: 5,
    pitch: 0,
    bearing: 0
  });

  const ZOOM_THRESHOLD = 16; // Seules les parcelles sont affichées à partir de ce zoom

  // Charger Turf.js pour les calculs géospatiaux (centroïde, bbox)
  const turfScript = document.createElement("script");
  turfScript.src = "https://cdn.jsdelivr.net/npm/@turf/turf/turf.min.js";
  document.head.appendChild(turfScript);

  map.on("load", () => {
    console.log("La carte est chargée.");

    // **************************
    // Couches des parcelles cadastrales
    // **************************
    map.addSource("cadastre", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/cadastre.json"
    });
    map.addLayer({
      id: "parcelles-layer",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      minzoom: ZOOM_THRESHOLD,
      paint: {
        "fill-color": "#50CEA8",
        "fill-opacity": 0.4,
        "fill-outline-color": "#000"
      }
    });
    console.log("Couche parcelles ajoutée.");

    // **************************
    // Source des limites administratives
    // **************************
    map.addSource("admin", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/decoupage-administratif.json"
    });

    // --- Pour les régions : on n'ajoute PAS de couche "visible" avec remplissage
    // On ajoute uniquement la couche "clickable" pour interagir
    map.addLayer({
      id: "regions-clickable",
      type: "fill",
      source: "admin",
      "source-layer": "regions",
      paint: {
        "fill-color": "#000000",
        "fill-opacity": 0.0
      }
    });
    // On peut ajouter en option un contour en ligne (facultatif)
    map.addLayer({
      id: "regions-line",
      type: "line",
      source: "admin",
      "source-layer": "regions",
      paint: {
        "line-color": "#FFAA00",
        "line-width": 2
      }
    });
    console.log("Couches pour les régions ajoutées.");

    // --- Pour les départements : affichage en ligne
    // Ajout d'une propriété minzoom pour forcer leur visibilité dès un certain niveau
    map.addLayer({
      id: "departements-layer",
      type: "line",
      source: "admin",
      "source-layer": "departements", // Assurez-vous que ce nom correspond à vos données
      minzoom: 5,
      paint: {
        "line-color": "#FFA500",  // Couleur orange par exemple
        "line-width": 2
      }
    });
    // Couche "clickable" pour les départements (fill transparent)
    map.addLayer({
      id: "departements-clickable",
      type: "fill",
      source: "admin",
      "source-layer": "departements",
      paint: {
        "fill-color": "#000000",
        "fill-opacity": 0.0
      }
    });
    console.log("Couches pour les départements ajoutées.");

    // --- Pour les communes : affichage en lignes
    map.addLayer({
      id: "communes-layer",
      type: "line",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "line-color": "#50CEA8",
        "line-width": 1
      }
    });
    // Couche "clickable" pour les communes
    map.addLayer({
      id: "communes-clickable",
      type: "fill",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "fill-color": "#000000",
        "fill-opacity": 0.0
      }
    });
    console.log("Couches pour les communes ajoutées.");

    // **************************
    // Couche de surbrillance pour la parcelle sélectionnée (prioritaire)
    // **************************
    map.addLayer({
      id: "selected-parcelle",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      filter: ["==", "id", ""],
      paint: {
        "fill-color": "#40A586",
        "fill-outline-color": "#000000",
        "fill-opacity": 0.7
      }
    });
    console.log("Couche de surbrillance ajoutée.");

    // **************************
    // Réorganisation des couches pour s'assurer que les couches "clickable" soient au-dessus
    // **************************
    map.moveLayer("communes-clickable", "selected-parcelle");
    map.moveLayer("departements-clickable", "communes-clickable");
    map.moveLayer("regions-clickable", "departements-clickable");

    // **************************
    // Gestion du curseur pour les zones interactives
    // **************************
    addCursorChange("regions-clickable", map);
    addCursorChange("departements-clickable", map);
    addCursorChange("communes-clickable", map);
    addCursorChange("parcelles-layer", map);

    // **************************
    // Fonction utilitaire pour zoomer sur une feature
    // **************************
    const zoomToFeature = (feature, targetZoom) => {
      const centroid = turf.centroid(feature).geometry.coordinates;
      // Effectuer le zoom uniquement si le niveau actuel est inférieur au niveau cible
      if (map.getZoom() < targetZoom - 0.5) {
        console.log("Zoom vers", targetZoom, "pour la feature", feature);
        map.flyTo({ center: centroid, zoom: targetZoom, duration: 1000 });
      } else {
        console.log("Niveau de zoom déjà suffisant :", map.getZoom());
      }
    };

    // **************************
    // Événements de zoom pour chaque niveau administratif
    // **************************

    // --- RÉGIONS ---
    map.on("click", "regions-clickable", (e) => {
      console.log("Clic sur région détecté", e);
      if (!e.features || !e.features.length) return;
      const feature = e.features[0];
      zoomToFeature(feature, TARGET_ZOOM.region);
    });

    // --- DÉPARTEMENTS ---
    map.on("click", "departements-clickable", (e) => {
      console.log("Clic sur département détecté", e);
      if (!e.features || !e.features.length) return;
      const feature = e.features[0];
      zoomToFeature(feature, TARGET_ZOOM.departement);
    });

    // --- COMMUNES ---
    map.on("click", "communes-clickable", (e) => {
      console.log("Clic sur commune détecté", e);
      if (!e.features || !e.features.length) return;
      if (map.getZoom() < TARGET_ZOOM.commune - 0.5) {
        const feature = e.features[0];
        zoomToFeature(feature, TARGET_ZOOM.commune);
      } else {
        console.log("Déjà assez zoomé sur la commune :", map.getZoom());
      }
    });

    // **************************
    // Sélection d'une parcelle (prioritaire)
    // **************************
    map.on("click", "parcelles-layer", (e) => {
      console.log("Clic sur parcelle détecté", e);
      if (!e.features || !e.features.length) {
        console.warn("Aucune parcelle détectée lors du clic.");
        return;
      }
      // Empêcher la propagation afin d'éviter un zoom sur la commune en même temps
      e.originalEvent.stopPropagation();

      const feature = e.features[0];
      const featureId = feature.properties.id || feature.properties.OBJECTID || feature.properties.fid;
      if (!featureId) {
        console.warn("Aucun identifiant unique trouvé pour la parcelle.");
        return;
      }
      map.setFilter("selected-parcelle", ["==", "id", featureId]);
      console.log("Parcelle sélectionnée, ID =", featureId);

      const centroid = turf.centroid(feature).geometry.coordinates;
      const prefixe = feature.properties.prefixe ? feature.properties.prefixe + " " : "";
      const numero  = feature.properties.numero ? feature.properties.numero + " " : "";
      const section = feature.properties.section ? feature.properties.section + ", " : "";
      const commune = feature.properties.commune || "";
      const adresse = prefixe + numero + section + commune;
      console.log("Adresse construite :", adresse);

      let parcelData = {
        x: centroid[0],
        y: centroid[1],
        adresse: adresse,
        geoloc_calcul: {
          "type": "Point",
          "coordinates": centroid
        },
        ...feature.properties
      };

      const reverseUrl = `https://data.geopf.fr/geocodage/reverse?lat=${centroid[1]}&lon=${centroid[0]}&returntruegeometry=false`;
      console.log("Appel reverse geocoding :", reverseUrl);

      fetch(reverseUrl)
        .then(response => {
          if (!response.ok) {
            throw new Error(`Erreur HTTP lors du reverse geocoding: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          console.log("Réponse reverse geocoding :", data);
          if (data.features && data.features.length > 0) {
            const adresseComplete = data.features[0].properties.label;
            parcelData.adresse_complete = adresseComplete;
            console.log("Adresse complète obtenue :", adresseComplete);
          }
          window.variables['12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données parcelle mises à jour :", window.variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        })
        .catch(error => {
          console.error("Erreur reverse geocoding :", error);
          window.variables['12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données parcelle mises à jour (sans adresse complète) :", window.variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        });
    });
  });
};

document.head.appendChild(script);

