        "fill-opacity": 0.3,
        "fill-outline-color": "#000",
      },
    });

    // Source pour les limites administratives (communes)
    map.addSource("admin", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/decoupage-administratif.json",
    });
    map.addLayer({
      id: "communes-layer",
      type: "line",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "line-color": "#39FF14",
        "line-width": 1,
      },
    });

    // (Optionnel) Couche pour afficher les sections dès le zoom 11
    map.addLayer({
      id: "sections-layer",
      type: "line",
      source: "cadastre",
      "source-layer": "sections",
      minzoom: 11,
      paint: {
        "line-color": "#00ff00",
        "line-width": 1,
      },
    });
  });

  // Ajout d'un debounce sur moveend si nécessaire
  map.on("moveend", () => {
    if (debounceTimeout) clearTimeout(debounceTimeout);
    debounceTimeout = setTimeout(() => {
      // Vous pouvez mettre ici des appels pour mettre à jour vos données en fonction du centre.
    }, 300);
  });
};

