const css = document.createElement("link");
css.href = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.css";
css.rel = "stylesheet";
document.head.appendChild(css);

// Charger Mapbox GL JS dynamiquement
const script = document.createElement("script");
script.src = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.js";
script.onload = () => {
  mapboxgl.accessToken =
    'pk.eyJ1Ijoib2FzMTg5MyIsImEiOiJjbGZkNjNtcmwwOTlzNDBscjNpYmZ4cWhwIn0.oGxBPKe7HPex0VJFW_Usow';

  // Création de la carte centrée sur la France (vue d'ensemble) dans le conteneur "map2"
  const map2 = new mapboxgl.Map({
    container: 'map2', // ID de votre div
    style: 'mapbox://styles/mapbox/light-v10',
    center: [2.35, 48.85],
    zoom: 13
  });

  const ZOOM_THRESHOLD = 16;
  let debounceTimeout = null;
  let measurementMarker = null; // Marqueur affichant la mesure (aire ou distance)

  // Charger Turf.js dynamiquement pour les calculs géospatiaux
  const turfScript = document.createElement("script");
  turfScript.src = "https://cdn.jsdelivr.net/npm/@turf/turf/turf.min.js";
  document.head.appendChild(turfScript);

  // Charger Mapbox Draw CSS dynamiquement
  const cssDraw = document.createElement("link");
  cssDraw.href = "https://api.mapbox.com/mapbox-gl-js/plugins/mapbox-gl-draw/v1.4.2/mapbox-gl-draw.css";
  cssDraw.rel = "stylesheet";
  document.head.appendChild(cssDraw);

  // Charger Mapbox Draw JS dynamiquement
  const scriptDraw = document.createElement("script");
  scriptDraw.src = "https://api.mapbox.com/mapbox-gl-js/plugins/mapbox-gl-draw/v1.4.2/mapbox-gl-draw.js";
  scriptDraw.onload = () => {
    // Initialiser Mapbox Draw avec uniquement les outils polygone, ligne et poubelle
    const Draw = new MapboxDraw({
      displayControlsDefault: false,
      controls: {
        polygon: true,
        line_string: true,
        trash: true,
      },
    });
    map2.addControl(Draw);

    map2.on("draw.create", () => {
      const data = Draw.getAll();
      if (data.features.length === 0) return;

      // Récupérer la feature dessinée
      const feature = data.features[0];

      // Supprimer d'éventuelles mesures précédentes
      if (measurementMarker) {
        measurementMarker.remove();
        measurementMarker = null;
      }
      // Si un polygone existe, supprimer toute ligne statique préexistante
      if (feature.geometry.type === "Polygon") {
        if (map2.getLayer("static-measurement-layer")) {
          map2.removeLayer("static-measurement-layer");
          map2.removeSource("static-measurement");
        }
      }
      // Si c'est une ligne, supprimer toute zone statique préexistante
      else if (feature.geometry.type === "LineString") {
        if (map2.getLayer("static-polygon-layer")) {
          map2.removeLayer("static-polygon-layer");
          map2.removeSource("static-polygon");
        }
      }

      // Si le type est un polygone : calculer l'aire
      if (feature.geometry.type === "Polygon") {
        const area = turf.area(feature); // Aire en m²
        const centroid = turf.centroid(feature); // Calcul du centroïde
        const coordinates = centroid.geometry.coordinates;

        const el = document.createElement("div");
        el.style.background = "rgba(0,0,0,0.7)";
        el.style.color = "#fff";
        el.style.padding = "5px 8px";
        el.style.borderRadius = "3px";
        el.style.fontSize = "12px";
        el.style.whiteSpace = "nowrap";
        el.innerHTML = `Aire: ${area.toFixed(2)} m²`;

        measurementMarker = new mapboxgl.Marker({ element: el, anchor: "center" })
          .setLngLat(coordinates)
          .addTo(map2);

        // Finaliser le dessin en supprimant la feature active de Draw
        Draw.deleteAll();

        // Affichage du polygone en couche statique
        if (map2.getSource("static-polygon")) {
          map2.getSource("static-polygon").setData(feature);
        } else {
          map2.addSource("static-polygon", {
            type: "geojson",
            data: feature,
          });
          map2.addLayer({
            id: "static-polygon-layer",
            type: "fill",
            source: "static-polygon",
            paint: {
              "fill-color": "#00ff00", // Vert fluo
              "fill-opacity": 0.3,
              "fill-outline-color": "#000",
            },
          });
        }
      }
      // Si le type est une ligne : calculer la distance
      else if (feature.geometry.type === "LineString") {
        const lengthKm = turf.length(feature, { units: "kilometers" });
        const lengthM = lengthKm * 1000;

        const coords = feature.geometry.coordinates;
        const midpoint = turf.midpoint(coords[0], coords[coords.length - 1]);

        const el = document.createElement("div");
        el.style.background = "rgba(0,0,0,0.7)";
        el.style.color = "#fff";
        el.style.padding = "5px 8px";
        el.style.borderRadius = "3px";
        el.style.fontSize = "12px";
        el.style.whiteSpace = "nowrap";
        el.innerHTML = `Distance: ${lengthM.toFixed(2)} m`;

        measurementMarker = new mapboxgl.Marker({ element: el, anchor: "center" })
          .setLngLat(midpoint.geometry.coordinates)
          .addTo(map2);

        Draw.deleteAll();

        if (map2.getSource("static-measurement")) {
          map2.getSource("static-measurement").setData(feature);
        } else {
          map2.addSource("static-measurement", {
            type: "geojson",
            data: feature,
          });
          map2.addLayer({
            id: "static-measurement-layer",
            type: "line",
            source: "static-measurement",
            paint: {
              "line-color": "#ff0000",
              "line-width": 2,
            },
          });
        }
      }
    });
  };
  document.head.appendChild(scriptDraw);

  // Préparation des sources et couches cadastrales
  map2.on("load", () => {
    // Ajout de la source vectorielle pour le cadastre via le TileJSON d'OpenMapTiles
    map2.addSource("cadastre", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/cadastre.json",
    });

    // Couche pour afficher les parcelles cadastrales (visible seulement à partir du zoom 16)
    map2.addLayer({
      id: "parcelles-layer",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      minzoom: ZOOM_THRESHOLD,
      paint: {
        "fill-color": "#39FF14", // Vert fluo
        "fill-opacity": 0.3,
        "fill-outline-color": "#000",
      },
    });

    // Source pour les limites administratives (communes)
    map2.addSource("admin", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/decoupage-administratif.json",
    });
    map2.addLayer({
      id: "communes-layer",
      type: "line",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "line-color": "#39FF14", // Vert fluo
        "line-width": 1,
      },
    });
  });

  // Optionnel: ajouter un debounce sur moveend pour déclencher des mises à jour si nécessaire
  map2.on("moveend", () => {
    if (debounceTimeout) clearTimeout(debounceTimeout);
    debounceTimeout = setTimeout(() => {
      // Ici, vous pouvez ajouter des appels pour recharger ou mettre à jour des données en fonction du centre.
    }, 300);
  });
};

document.head.appendChild(script);

