// Déclarez la variable globale "variables" (si elle n'existe pas déjà)
window.variables = window.variables || {};

// Charger Mapbox GL CSS dynamiquement
const css = document.createElement("link");
css.href = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.css";
css.rel = "stylesheet";
document.head.appendChild(css);

// Charger Mapbox GL JS dynamiquement
const script = document.createElement("script");
script.src = "https://api.mapbox.com/mapbox-gl-js/v2.15.0/mapbox-gl.js";
script.onload = () => {
  mapboxgl.accessToken =
    'pk.eyJ1Ijoib2FzMTg5MyIsImEiOiJjbGZkNjNtcmwwOTlzNDBscjNpYmZ4cWhwIn0.oGxBPKe7HPex0VJFW_Usow';

  // Création de la carte centrée sur la France (vue d'ensemble)
  const map = new mapboxgl.Map({
    container: 'map', // Assurez-vous d'avoir une div avec l'ID "map"
    style: 'mapbox://styles/mapbox/light-v10',
    center: [2.0, 46.5],  // Coordonnées approximatives du centre de la France
    zoom: 5,              // Niveau de zoom pour voir la France dans son ensemble
    pitch: 0,
    bearing: 0
  });

  // Rendre l'instance de la carte accessible globalement
  window.map = map;

  const ZOOM_THRESHOLD = 12;

  // Charger Turf.js pour les calculs géospatiaux (pour le centroïde)
  const turfScript = document.createElement("script");
  turfScript.src = "https://cdn.jsdelivr.net/npm/@turf/turf/turf.min.js";
  document.head.appendChild(turfScript);

  map.on("load", () => {
    console.log("La carte est chargée.");

    // Source et couche des parcelles cadastrales (uniquement le remplissage)
    map.addSource("cadastre", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/cadastre.json"
    });
    map.addLayer({
      id: "parcelles-layer",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      minzoom: ZOOM_THRESHOLD,
      paint: {
        "fill-color": "#50CEA8",        // Couleur du fond
        "fill-opacity": 0.1               // Opacité appliquée uniquement au fond
        // Pas de "fill-outline-color"
      }
    });
    console.log("Couche parcelles ajoutée (seulement le remplissage).");

    // Ajout d'un layer supplémentaire pour le contour en gris foncé non opaque
    map.addLayer({
      id: "parcelles-outline",
      type: "line",
      source: "cadastre",
      "source-layer": "parcelles",
      minzoom: ZOOM_THRESHOLD,
      paint: {
        "line-color": "rgba(51,51,51,0.5)", // Gris foncé avec opacité 50%
        "line-width": 1                    // Épaisseur fine
      }
    });
    console.log("Couche de contour ajoutée.");

    // Source et couche des limites administratives (communes, départements, régions)
    map.addSource("admin", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/decoupage-administratif.json"
    });

    // Couche pour les régions avec un contour de 3px
    map.addLayer({
      id: "regions-layer",
      type: "line",
      source: "admin",
      "source-layer": "regions",
      paint: {
        "line-color": "#50CEA8",
        "line-width": 2
      }
    });
    console.log("Couche régions ajoutée.");

    // Couche pour les départements avec un contour de 2px
    map.addLayer({
      id: "departements-layer",
      type: "line",
      source: "admin",
      "source-layer": "departements",
      paint: {
        "line-color": "#50CEA8",
        "line-width": 1
      }
    });
    console.log("Couche départements ajoutée.");

    // Couche pour les communes avec un contour de 1px
    map.addLayer({
      id: "communes-layer",
      type: "line",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "line-color": "#50CEA8",
        "line-width": 1
      }
    });
    console.log("Couche communes ajoutée.");

    // Couche de surbrillance pour la parcelle sélectionnée
    map.addLayer({
      id: "selected-parcelle",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      filter: ["==", "id", ""], // Aucune parcelle sélectionnée initialement
      paint: {
        "fill-color": "#40A586",        // Couleur plus sombre pour la sélection
        "fill-outline-color": "#000000",  // Contour noir foncé
        "fill-opacity": 0.7
      }
    });
    console.log("Couche de surbrillance ajoutée.");

    // Change le curseur pour montrer que la couche est interactive
    map.on("mouseenter", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "pointer";
    });
    map.on("mouseleave", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "";
    });

    // Au clic sur une parcelle
    map.on("click", "parcelles-layer", (e) => {
      console.log("Clic détecté sur la couche parcelles.");
      if (!e.features || !e.features.length) {
        console.warn("Aucune feature détectée lors du clic.");
        return;
      }
      const feature = e.features[0];
      console.log("Feature cliquée:", feature);

      // Récupérer un identifiant unique parmi plusieurs possibilités
      const featureId = feature.properties.id || feature.properties.OBJECTID || feature.properties.fid;
      if (!featureId) {
        console.warn("Aucun identifiant unique trouvé pour la feature cliquée.");
        return;
      }
      console.log("Identifiant de la feature:", featureId);

      // Mettre à jour le filtre de la couche de surbrillance pour ne sélectionner que cette feature
      map.setFilter("selected-parcelle", ["==", "id", featureId]);
      console.log("Filtre de surbrillance mis à jour.");

      // Calculer le centroïde avec Turf.js pour obtenir les coordonnées (longitude, latitude)
      const centroid = turf.centroid(feature);
      const coordinates = centroid.geometry.coordinates;
      console.log("Centroïde calculé:", coordinates);

      // Construction d'une adresse à partir des propriétés disponibles
      const prefixe = feature.properties.prefixe ? feature.properties.prefixe + " " : "";
      const numero  = feature.properties.numero ? feature.properties.numero + " " : "";
      const section = feature.properties.section ? feature.properties.section + ", " : "";
      const commune = feature.properties.commune || "";
      const adresse = prefixe + numero + section + commune;
      console.log("Adresse construite:", adresse);

      // Construction de l'objet de données de la parcelle, incluant la clé "geoloc_calcul"
      let parcelData = {
        x: coordinates[0],    // Longitude
        y: coordinates[1],    // Latitude
        adresse: adresse,     // Adresse construite
        geoloc_calcul: {      // Ajout de la clé "geoloc_calcul" avec la géométrie sous forme de GeoJSON Point
          "type": "Point",
          "coordinates": coordinates
        },
        ...feature.properties // Toutes les autres informations disponibles
      };

      // Appel du service de géocodage inverse IGN pour récupérer une adresse complète
      const reverseUrl = `https://data.geopf.fr/geocodage/reverse?lat=${coordinates[1]}&lon=${coordinates[0]}&returntruegeometry=false`;
      console.log("Appel au service de géocodage inverse:", reverseUrl);

      fetch(reverseUrl)
        .then(response => {
          if (!response.ok) {
            throw new Error(`Erreur HTTP lors du reverse geocoding: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          console.log("Réponse du reverse geocoding:", data);
          if (data.features && data.features.length > 0) {
            const adresseComplete = data.features[0].properties.label;
            parcelData.adresse_complete = adresseComplete;
            console.log("Adresse complète obtenue:", adresseComplete);
          } else {
            console.warn("Aucune adresse complète trouvée dans la réponse.");
          }
          // Mise à jour de la variable globale avec les données complètes de la parcelle
          variables['12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données de la parcelle mises à jour:", variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        })
        .catch(error => {
          console.error("Erreur lors du reverse geocodage:", error);
          // Même en cas d'erreur, met à jour la variable avec les données disponibles
          variables['12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données de la parcelle mises à jour (sans adresse complète):", variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        });
    });
  });
};

document.head.appendChild(script);

