      filter: ["==", "id", ""],
      paint: {
        "fill-color": "#ffffff",       // Fond en blanc pour la sélection
        "fill-outline-color": "#000000", // Contour noir
        "fill-opacity": 0.5
      }
    });

    // Changement du curseur pour indiquer l'interactivité sur la couche des parcelles
    map.on("mouseenter", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "pointer";
    });
    map.on("mouseleave", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "";
    });

    // Au clic sur une parcelle
    map.on("click", "parcelles-layer", (e) => {
      if (!e.features.length) return;
      const feature = e.features[0];

      // Debug : Afficher dans la console les propriétés de la feature
      console.log("Feature cliquée:", feature);
      // Tenter de trouver un identifiant unique parmi plusieurs possibilités
      const featureId = feature.id || feature.properties.id || feature.properties.OBJECTID || feature.properties.fid;
      if (!featureId) {
        console.warn("Aucun identifiant unique trouvé pour la feature cliquée.");
        return;
      }

      // Mettre à jour le filtre de la couche de surbrillance pour ne sélectionner que cette feature
      map.setFilter("selected-parcelle", ["==", "id", featureId]);

      // Calculer le centroïde avec Turf.js pour positionner le pin
      const centroid = turf.centroid(feature);
      const coordinates = centroid.geometry.coordinates;

      // Créer ou déplacer le marqueur "pin"
      if (selectionMarker) {
        selectionMarker.setLngLat(coordinates);
      } else {
        const pinEl = document.createElement("div");
        pinEl.style.width = "20px";
        pinEl.style.height = "20px";
        pinEl.style.background = "red";
        pinEl.style.borderRadius = "50%";
        pinEl.style.border = "2px solid black";
        // Le pin est positionné à partir du bas (pour ressembler à une pointe)
        selectionMarker = new mapboxgl.Marker({ element: pinEl, anchor: "bottom" })
          .setLngLat(coordinates)
          .addTo(map);
      }
    });
  });
};

document.head.appendChild(script);

