        "fill-opacity": 0.4,              // Opacité élevée
        "fill-outline-color": "#000"      // Contour noir
      }
    });
    console.log("Couche parcelles ajoutée.");

    // Source et couche des limites administratives (communes)
    map.addSource("admin", {
      type: "vector",
      url: "https://openmaptiles.geo.data.gouv.fr/data/decoupage-administratif.json"
    });
    map.addLayer({
      id: "communes-layer",
      type: "line",
      source: "admin",
      "source-layer": "communes",
      paint: {
        "line-color": "#50CEA8",         // Contour vert selon demande
        "line-width": 1
      }
    });
    console.log("Couche communes ajoutée.");

    // Couche de surbrillance pour la parcelle sélectionnée
    map.addLayer({
      id: "selected-parcelle",
      type: "fill",
      source: "cadastre",
      "source-layer": "parcelles",
      filter: ["==", "id", ""], // Aucune parcelle sélectionnée initialement
      paint: {
        "fill-color": "#40A586",        // Couleur plus sombre que 50CEA8 pour la sélection
        "fill-outline-color": "#000000",  // Contour noir foncé
        "fill-opacity": 0.7
      }
    });
    console.log("Couche de surbrillance ajoutée.");

    // Change le curseur pour montrer que la couche est interactive
    map.on("mouseenter", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "pointer";
    });
    map.on("mouseleave", "parcelles-layer", () => {
      map.getCanvas().style.cursor = "";
    });

    // Au clic sur une parcelle
    map.on("click", "parcelles-layer", (e) => {
      console.log("Clic détecté sur la couche parcelles.");
      if (!e.features || !e.features.length) {
        console.warn("Aucune feature détectée lors du clic.");
        return;
      }
      const feature = e.features[0];
      console.log("Feature cliquée:", feature);

      // Récupérer un identifiant unique parmi plusieurs possibilités
      const featureId = feature.properties.id || feature.properties.OBJECTID || feature.properties.fid;
      if (!featureId) {
        console.warn("Aucun identifiant unique trouvé pour la feature cliquée.");
        return;
      }
      console.log("Identifiant de la feature:", featureId);

      // Mettre à jour le filtre de la couche de surbrillance pour ne sélectionner que cette feature
      map.setFilter("selected-parcelle", ["==", "id", featureId]);
      console.log("Filtre de surbrillance mis à jour.");

      // Calculer le centroïde avec Turf.js pour obtenir les coordonnées (longitude, latitude)
      const centroid = turf.centroid(feature);
      const coordinates = centroid.geometry.coordinates;
      console.log("Centroïde calculé:", coordinates);

      // Construction d'une adresse à partir des propriétés disponibles
      const prefixe = feature.properties.prefixe ? feature.properties.prefixe + " " : "";
      const numero  = feature.properties.numero ? feature.properties.numero + " " : "";
      const section = feature.properties.section ? feature.properties.section + ", " : "";
      const commune = feature.properties.commune || "";
      const adresse = prefixe + numero + section + commune;
      console.log("Adresse construite:", adresse);

      // Construction de l'objet de données de la parcelle
      let parcelData = {
        x: coordinates[0],    // Longitude
        y: coordinates[1],    // Latitude
        adresse: adresse,     // Adresse construite à partir des propriétés
        ...feature.properties // Toutes les autres informations disponibles sur la parcelle
      };

      // Appel du service de géocodage inverse IGN pour récupérer une adresse complète
      const reverseUrl = `https://data.geopf.fr/geocodage/reverse?lat=${coordinates[1]}&lon=${coordinates[0]}&returntruegeometry=false`;
      console.log("Appel au service de géocodage inverse:", reverseUrl);

      fetch(reverseUrl)
        .then(response => {
          if (!response.ok) {
            throw new Error(`Erreur HTTP lors du reverse geocoding: ${response.status}`);
          }
          return response.json();
        })
        .then(data => {
          console.log("Réponse du reverse geocoding:", data);
          if (data.features && data.features.length > 0) {
            const adresseComplete = data.features[0].properties.label;
            parcelData.adresse_complete = adresseComplete;
            console.log("Adresse complète obtenue:", adresseComplete);
          } else {
            console.warn("Aucune adresse complète trouvée dans la réponse.");
          }
          // Mise à jour de la variable globale avec les données complètes de la parcelle
          variables[/* value_selected */'12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données de la parcelle mises à jour:", window.variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        })
        .catch(error => {
          console.error("Erreur lors du reverse geocodage:", error);
          // Même en cas d'erreur, met à jour la variable avec les données disponibles
          variables[/* value_selected */'12af8049-2433-4cd1-961b-3606daff15c8'] = parcelData;
          console.log("Données de la parcelle mises à jour (sans adresse complète):", window.variables['12af8049-2433-4cd1-961b-3606daff15c8']);
        });
    });
  });
};

