function startDistanceDraw() {
  if (!window.map2 || typeof MapboxDraw === 'undefined') return;

  if (!window.draw) {
    window.draw = new MapboxDraw({
      displayControlsDefault: false,
      controls: { line_string: true, trash: false }  // ← outil ligne seule
    });
    map2.addControl(window.draw);
  }

  window.draw.deleteAll();               // vide les éventuelles lignes
  window.draw.changeMode('draw_line_string');
}

/* 3) Lance immédiatement si la carte est prête, sinon à l’événement custom */
if (window.map2 && window.map2.loaded()) {
  startDistanceDraw();
} else {
  window.addEventListener('map2-ready', startDistanceDraw, { once: true });
}

